import React, { Component } from "react";

import BlogPost from "../common/BlogPost";

import "./styles.scss";


class Carousel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      index: 0
    }
  }

  componentDidMount() {
    const { posts } = this.props;
    this.setState({
      index: posts.length/2
    })
  }

  render() {
    return (
      <div className="carousel">

      </div>
    );
  }
}

